/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "scaffold-a-docz-for-use-quickly"
    }}>{`Scaffold a DOCZ for use quickly`}</h1>
    <p>{`Possible improvements:
`}<a parentName="p" {...{
        "href": "https://www.npmjs.com/package/docz-iframe-playground"
      }}>{`docz-iframe-playground`}</a></p>
    <blockquote>
      <h3 parentName="blockquote" {...{
        "id": "this-doc-will-show-you-how-to-quickly-scaffold-a-docs-app-without-having-to-decrypt-the-official-documents-first-following-theses-steps-will-have-you-up-and-running-in-no-time"
      }}>{`This doc will show you how to quickly scaffold a docs app without having to decrypt the official documents first. following theses steps will have you up and running in no time`}</h3>
    </blockquote>
    <ul>
      <li parentName="ul"><h4 parentName="li" {...{
          "id": "prepare"
        }}>{`Prepare:`}</h4><pre parentName="li"><code parentName="pre" {...{}}>{`    Setup git repo and '.gitignore' and enter 'node_modules'.
`}</code></pre></li>
    </ul>
    <ol>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "setup-project"
        }}>{`Setup project`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`mkdir MyDevDocz
cd MyDevDocz
npm init -y 
npm install react react-doc
npm install docz docz-theme-default
npm update all
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-scripts-in-packagejson"
        }}>{`Add scripts in `}<inlineCode parentName="h3">{`package.json`}</inlineCode></h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-json"
          }}>{`"scripts": {
    "start": "docz dev --port 5001",
    "build": "docz build"
}
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add--page-in-root-dir"
        }}>{`Add  page in root dir`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`$ echo "# My Dev Docz" > index.mdx
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add--build-folder-to-gitignore-echo-docz--gitignore"
        }}>{`Add  build folder to gitignore `}<inlineCode parentName="h3">{`echo \\.docz >> .gitignore`}</inlineCode></h3>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-doczrcjs-config-file"
        }}>{`Add `}<inlineCode parentName="h3">{`doczrc.js`}</inlineCode>{` config file.`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`    echo "export default {title: 'My Dev Docz',description: 'This is my awesome documentation',theme: 'docz-theme-default'}" > doczrc.js
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "create-env-file-with-content"
        }}>{`Create `}<inlineCode parentName="h3">{`.env`}</inlineCode>{` file with content:`}</h3>
        <pre parentName="li"><code parentName="pre" {...{}}>{`DOCZ_BASE || '/'
DOCZ_DEBUG || true
DOCZ_SOURCE || './src'
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "create-source-directory"
        }}>{`Create source directory`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`mkdir src/pages; mv index.mdx src/pages/index.mdx
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "install-environment-variables-package"
        }}>{`Install environment variables package`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`npm install dotenv
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-config-entries"
        }}>{`Add config entries:`}</h3>
        <pre parentName="li"><code parentName="pre" {...{}}>{`files: 'src/**/*.{md, markdown, mdx}'
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-environment-variable-to-env"
        }}>{`Add environment variable to `}<inlineCode parentName="h3">{`.env`}</inlineCode></h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`NODE_ENV=src
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "create-html-index-file-in-public-folder"
        }}>{`Create html index file in public folder`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`    mkdir public
`}</code></pre>
        <h5 parentName="li" {...{
          "id": "create-indexhtml-in-public-and-populate-with"
        }}>{`Create `}<inlineCode parentName="h5">{`index.html`}</inlineCode>{` in public and populate with:`}</h5>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-html"
          }}>{`
    <!DOCTYPE html>
    <html lang="{{ lang }}">
    <head>
        <meta charset="UTF-8">
        <meta name="description" content="{{ description }}">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <meta http-equiv="X-UA-Compatible" content="ie=edge">
        <title>{{ title }}</title>
        {{ head }}
    </head>
    <body>
        <div id="root" />
        {{ footer }}
    </body>
    </html>

`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-entries-to-config-file"
        }}>{`Add entries to config file:`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-js"
          }}>{`    public: '/public',
    indexHtml: 'public/index.html'
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "create-nav-menu-config-for-ordering-correctly"
        }}>{`Create nav menu config for ordering correctly`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-bash"
          }}>{`    mkdir src / menu
    echo "module.exports = ['Landing', 'Page Z', 'Page A']" > src / menu / index.js
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "modify-doczrcjs-add-entry"
        }}>{`Modify `}<inlineCode parentName="h3">{`doczrc.js`}</inlineCode>{` add entry`}</h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-js"
          }}>{`menu: require('./src/menu')
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "modify-srcpagesindexmdx"
        }}>{`Modify `}<inlineCode parentName="h3">{`src/pages/index.mdx`}</inlineCode></h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-md"
          }}>{`    ---
    name: Landing
    route: /landing
    ---

    # My Dec Docz

    ### info goes here
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "create-pages-page1mdx-and-page2mdx"
        }}>{`Create pages `}<inlineCode parentName="h3">{`page1.mdx`}</inlineCode>{` and `}<inlineCode parentName="h3">{`page2.mdx`}</inlineCode></h3>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-content-page1mdx"
        }}>{`Add Content `}<inlineCode parentName="h3">{`page1.mdx`}</inlineCode></h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-md"
          }}>{`    ---
    name: Page Z
    route: /page1
    ---

    # Page One
`}</code></pre>
      </li>
      <li parentName="ol">
        <h3 parentName="li" {...{
          "id": "add-content-page2mdx"
        }}>{`Add Content `}<inlineCode parentName="h3">{`page2.mdx`}</inlineCode></h3>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-md"
          }}>{`---
name: Page A
route: /page2
---

# Page Two
`}</code></pre>
      </li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
